import React, { useState, useEffect } from 'react';

import logo from "../assets/logo.svg";

import { useAuth0, withAuthenticationRequired } from "@auth0/auth0-react";
import { render } from "react-dom";
import Loading from "./Loading";
import Button from "reactstrap/lib/Button";
import axios from 'axios';


const Order = () => {
    const {
        user,
        isAuthenticated,
        loginWithRedirect,
        logout
    } = useAuth0();

    const onTriggerVerifyEmail = () => {

    }

    let orderDetails = {},
        loading = true;


    useEffect(() => {
        axios.get('http://localhost:3001/order')
            .then(resp => {
                orderDetails = resp.data,
                    loading = false;
            })
    });

    return (
        <>

            {loading && <>  Please wait while we ginalise your order </>}
            {!loading && <>

                <h1>Thanks for your order</h1>

                {user.email_verified && <>
                    <h6 style={{ color: 'green' }}>1. You have already verified your email address</h6>
                    <br />

                    <h6 style={{ color: 'green' }}>2. Order Details</h6>

                    <br />
                    <br />

                    <h2>Your order is on it's way</h2>

                    <span>{JSON.stringify({
                        timestamp: "09876543",
                        orderId: "1234567876543"
                    }, null, 2)}</span>


                    <br />
                    <br />

                    <h4>1 x Summer Yummy Tummy Tickler</h4>
                    <p>Honestly, the bestpizza you've never had.</p>

                    <br />



                </>}

            </>}


        </>
    )
};

export default withAuthenticationRequired(Order, {
    onRedirecting: () => <Loading />,
});
