import React from "react";

import logo from "../assets/logo.svg";

import { useAuth0 } from "@auth0/auth0-react";
import { render } from "react-dom";



const Hero = () => {
    const {
        user,
        isAuthenticated,
        loginWithRedirect,
        logout,
    } = useAuth0();

    return (
        <>
            {!isAuthenticated && <>

                <div className="text-center hero my-5">
                    <img className="mb-3 app-logo" src={logo} alt="React logo" width="120" />
                    <h1 className="mb-4">Super Yummy Tummy Tickler</h1>

                    <p className="lead">
                        Quite simple the best pizza you've never had.</p>

                    <p><a>Sign up to order</a>
                    </p>
                </div>
            </>}

            {isAuthenticated && <>

                <div className="text-center hero my-5">
                    <img className="mb-3 app-logo" src={logo} alt="React logo" width="120" />
                    <h1 className="mb-4">Super Yummy Tummy Tickler</h1>

                    <p className="lead">
                        Quite simple the best pizza you've never had.</p>

                    <p><a href="https://reactjs.org">Order now</a>
                    </p>
                </div>
            </>}
        </>
    )
};

export default Hero;
