import React from "react";

import logo from "../assets/logo.svg";

import { useAuth0, withAuthenticationRequired } from "@auth0/auth0-react";
import { render } from "react-dom";
import Loading from "./Loading";
import { NavLink as RouterNavLink } from "react-router-dom";

import {
    Button,
    NavLink,
} from "reactstrap";
import axios from "axios";



const Order = (props) => {

    console.log ('order props', props);
    const {
        user,
        isAuthenticated,
        getAccessTokenSilently,
        loginWithRedirect,
        logout
    } = useAuth0();

    const onTriggerVerifyEmail = () => {

    }

    return (
        <>
            <h1>Place your order</h1>

            {!user.email_verified && <>
                <h4>1. Please verify your email before continuing</h4>
                <a onClick={onTriggerVerifyEmail}> Click here to resend email </a>

                <br />
                <br />

                <h6>2. Place your order</h6>
            </>}


            {user.email_verified && <>
                <h6 style={{ color: 'green' }}>1. You have already verified your email address</h6>
                <br />

                <h2>2. Order Details</h2>

                <br />
                <br />

                <h4>1 x Summer Yummy Tummy Tickler</h4>
                <p>Honestly, the bestpizza you've never had.</p>


                <br />

                <Button
                    onClick={ async () => {
                        const token = await getAccessTokenSilently();

                        axios.get('http://localhost:3001/api/order', {
                            headers: {
                                Authorization: `Bearer ${token}`
                            }
                        })

                            .then(resp => {

                                let orders = (user.app_metadata && typeof user.app_metadata.order  != 'undefined') ? user.app_metadata.orders: [];
                                axios.post('https://db-test.eu.auth0.com/api/v2/users/' + user.sub, {
                                    app_metadata: {
                                        ...user.app_metadata,
                                        orders: [
                                            ...orders,
                                            { ...resp.data }
                                        ]
                                    },
                                    headers: {
                                        Authorization: `Bearer ${token}`
                                    }
                                })


                            })
                    }}
                >
                    Order Now
                </Button>


            </>}

        </>
    )
};

export default withAuthenticationRequired(Order, {
    onRedirecting: () => <Loading />,
});
